INSERT INTO book (book_id, title, subcategory_id, publisher_id, price, point, published_date, description, image_url) VALUES (362, '[큰글자책] 원서발췌 해석학의 탄생', 12, 192, 30000, 900, '2025-11-20', '딜타이 ≪전집(Gesammelte Schriften)≫ 중에서 해석학의 탄생과 관련된 부분을 발췌·번역한 것이다. 각기 다른 세 개의 번역문을 관통하고 있는 핵심적 사유는 어떤 과정을 거쳐 근대에 ‘보편적 해석학’이 정립될 수 있었느냐 하는 것이다. 이 과정에서 단연 중심인물로 거론되는 사람이 슐라이어마허이다. 딜타이는 그를 독일의 위대한 철학자로 그리고 고전어의 대가로 존경과 흠모를 아끼지 않았다.', 'https://image.aladin.co.kr/product/37788/45/cover200/k502033567_1.jpg');
